#include "Arrow.h"Arrow* Arrow::createArrowWithLevel(int level) {    auto arrow = new Arrow();        if (arrow && arrow->initWithLevel(level)) {        arrow->autorelease();        return arrow;    }        CC_SAFE_DELETE(arrow);        return NULL;}bool Arrow::initWithLevel(int level) {    if ( ! ArrowBulletBase::initWithTypeAndLevel(BULLET_TYPE_ARROW, level) ) {		return false;	}	return true;}void Arrow::shoot() {	SoundManager::playArrowRelease();	this->runAction(Sequence::create(_bulletAction,                                     CallFuncN::create(CC_CALLBACK_0(Arrow::attackPointMonster, this)),                                     NULL));}void Arrow::processMonsterDeath(BaseMonster * monster) {    monster->death();}void Arrow::handleMissBullet() {    _bulletSprite->setSpriteFrame(SpriteFrameCache::getInstance()->getSpriteFrameByName("decal_arrow.png"));    _bulletSprite->runAction(Sequence::create(FadeOut::create(2.0f),                                              CallFuncN::create(CC_CALLBACK_0(Arrow::setVisible, this, false)),                                              FadeIn::create(0.0f),                                              NULL));}