#include "ArrowBase.h"#include "TowerUpdateManager.h"//用于由于武器升级造成的影响void ArrowBase::addAdditionalParameterConfig() {    this->setMaxForce(this->getMaxForce() * (1 + TowerUpdateManager::getInstance()->getTowerUpgradeValue(UpgradeTowerTagArrow01_ImproveForce)));    this->setBulletSpeed(this->getBulletSpeed() * (1 + TowerUpdateManager::getInstance()->getTowerUpgradeValue(UpgradeTowerTagArrow05_Speed)));}