#include "MageBase.h"#include "TowerUpdateManager.h"//用于由于武器升级造成的影响void MageBase::addAdditionalParameterConfig() {    this->setMaxForce(this->getMaxForce() * (1 + TowerUpdateManager::getInstance()->getTowerUpgradeValue(UpgradeTowerTagMage01_Force)));    this->setBulletSpeed(this->getBulletSpeed() * (1 + TowerUpdateManager::getInstance()->getTowerUpgradeValue(UpgradeTowerTagMage05_Speed)));}