#include "MageBolt.h"#include "GameManager.h"MageBolt* MageBolt::createMageBoltWithLevel(int level) {    auto mageBolt = new MageBolt();        if (mageBolt && mageBolt->initWithLevel(level)) {        mageBolt->autorelease();        return mageBolt;    }        CC_SAFE_DELETE(mageBolt);        return NULL;}bool MageBolt::initWithLevel(int level) {    if ( ! ArrowBulletBase::initWithTypeAndLevel(BULLET_TYPE_MAGABOLT, level) ) {        return false;    }        return true;}void MageBolt::shoot(){	this->runAction(Sequence::create(_bulletAction,                                     CallFuncN::create(CC_CALLBACK_0(MageBolt::attackPointMonster, this)),                                     NULL));}void MageBolt::processMonsterDeath(BaseMonster * monster) {    monster->death();}