#include "NecrBolt.h"bool NecrBolt::init() {	if (! ArrowBulletBase::initWithTypeAndLevel(BULLET_TYPE_NECRBOLT, 1)) {		return false;	}	return true;}void NecrBolt::shoot() {	this->scheduleUpdate();	this->runAction(Sequence::create(_bulletAction,                                     CallFuncN::create(CC_CALLBACK_0(NecrBolt::attackPointMonster, this)),                                     NULL));}