#include "BombBase.h"#include "Utility.h"#include "TowerUpdateManager.h"//用于由于武器升级造成的影响void BombBase::addAdditionalParameterConfig() {    this->setMaxForce(this->getMaxForce() * (1 + TowerUpdateManager::getInstance()->getTowerUpgradeValue(UpgradeTowerTagArtillery01_Force)));    this->setBulletScope(this->getBulletScope() * (1 + TowerUpdateManager::getInstance()->getTowerUpgradeValue(UpgradeTowerTagArtillery05_AttackScope)));}