#include "Freeze.h"#include "GameManager.h"bool Freeze::init(){	if(! BombBulletBase::initWithTypeAndLevel(BULLET_TYPE_FREEZE, 1)) {		return false;	}    //    this->setBulletType(BULLET_TYPE_FREEZE);//    //    this->addBulletSprite("small_freeze_bomb.png");	return true;}void Freeze::shoot(Point point){	this->setPosition(point);	_bulletSprite->runAction(Spawn::create(Sequence::create(MoveBy::create(0.5f, Point(0, 50)),                                                            MoveBy::create(0.5f, Point(0, - 50)),                                                            Animate::create(AnimationCache::getInstance()->getAnimation("freeze_explosion")),                                                            CallFuncN::create(CC_CALLBACK_0(Freeze::attackAreaMonster, this)),                                                            NULL),                                           RotateBy::create(1.0f, 1080.0f),                                           NULL));}void Freeze::handleHP(BaseMonster * monster) {    monster->frozen();}//void Freeze::removeBullet()//{//	auto instance = GameManager::getInstance();//    auto monsterVector = instance->monsterVector;////	for(int i = 0; i < monsterVector.size(); i++)//	{//		auto monster = monsterVector.at(i);//		double distance = (this->getPosition()).getDistance(monster->getMonsterPosition());////		if (monster != NULL && distance < 100)//			monster->frozen();//	}//	this->removeFromParent();//}