#include "BulletParameter.h"USING_NS_CC;BulletParameter* BulletParameter::createWithData(int type,                                       int level,                                       std::string baseImageName,                                       int force,                                       int scope,                                       int speed,                                       bool isArea,                                       bool isSoldier,                                       bool isTower) {    auto bulletParameter = new BulletParameter();    if (bulletParameter && bulletParameter->init()) {        bulletParameter->setType(type);        bulletParameter->setLevel(level);        bulletParameter->setBaseImageName(baseImageName);        bulletParameter->setForce(force);        bulletParameter->setScope(scope);        bulletParameter->setSpeed(speed);        bulletParameter->setIsArea(isArea);        bulletParameter->setIsSoldier(isSoldier);        bulletParameter->setIsTower(isTower);                return bulletParameter;            }        CC_SAFE_RELEASE(bulletParameter);        return NULL;}