#include "Lava.h"bool Lava::init(){	if (!Bullet::init())	{		return false;	}    this->setBulletType(BULLET_TYPE_LAVA);        this->addBulletSprite("EarthquakeTower_Lava.png");    	_bulletSprite->setScale(2.0f);	_bulletSprite->setPosition(Point(0, -20));	this->setVisible(false);	return true;}void Lava::shoot() {	this->setVisible(true);	this->runAction(Sequence::create(FadeOut::create(1.0f),                                     CallFuncN::create(CC_CALLBACK_0(Lava::setVisible, this, false)),                                     NULL));}