#include "Smoke.h"#include "GameManager.h"bool Smoke::init() {	if (!Bullet::init()) {		return false;	}		return true;}void Smoke::fadeSmoke(Animation* animation, float time) {	this->runAction(Sequence::create(Animate::create(animation),                                     DelayTime::create(time),                                     CallFuncN::create(CC_CALLBACK_0(Smoke::removeSmoke, this)),                                     NULL));}void Smoke::removeSmoke() {	this->removeFromParent();}