#include "CircleBase.h"bool CircleBase::initWithScaleXFactorAndFileName(float scaleXFactor, std::string filename) {	if (! Sprite::init()) {		return false;	}    _scaleXFactor = scaleXFactor;    	_circleLeftTop = Sprite::createWithSpriteFrameName(filename);    _circleLeftTop->setAnchorPoint(Vec2::ANCHOR_BOTTOM_RIGHT);    this->addChild(_circleLeftTop);        _circleRightTop = Sprite::createWithSpriteFrameName(filename);    _circleRightTop->setFlippedX(true);    _circleRightTop->setAnchorPoint(Point(Vec2::ANCHOR_BOTTOM_LEFT));    this->addChild(_circleRightTop);        _circleRightBottom = Sprite::createWithSpriteFrameName(filename);    _circleRightBottom->setFlippedX(true);    _circleRightBottom->setFlippedY(true);    _circleRightBottom->setAnchorPoint(Point(Vec2::ANCHOR_TOP_LEFT));    this->addChild(_circleRightBottom);        _circleLeftBottom = Sprite::createWithSpriteFrameName(filename);	_circleLeftBottom->setFlippedY(true);	_circleLeftBottom->setAnchorPoint(Point(Vec2::ANCHOR_TOP_RIGHT));	this->addChild(_circleLeftBottom);        this->setScaleXFactor(scaleXFactor);    	return true;}void CircleBase::setScaleXFactor(float scaleXFactor) {    _circleLeftTop->setScaleX(scaleXFactor);    _circleRightTop->setScaleX(scaleXFactor);    _circleLeftBottom->setScaleX(scaleXFactor);    _circleRightBottom->setScaleX(scaleXFactor);}Size CircleBase::getCircleSize() {    return this->_circleLeftTop->getContentSize();}