#include "PlayerStateLabel.h"#include "Utility.h"bool PlayerStateLabel::init() {    if( !Sprite::init() ){        return false;    }        this->setSpriteFrame("hud_background.png");    this->setAnchorPoint(Vec2::ANCHOR_TOP_LEFT);        _lifeLabel = Label::createWithBMFont("fonts/bmfont.fnt", "0");    _lifeLabel->setPosition(Point(this->getContentSize().width / 4,                                 this->getContentSize().height / 4 * 3));    this->addChild(_lifeLabel);        _goldLabel = Label::createWithBMFont("fonts/bmfont.fnt", "0");    _goldLabel->setPosition(Point(this->getContentSize().width / 4 * 3,                                 this->getContentSize().height / 4 * 3));    this->addChild(_goldLabel);        _waveLabel = Label::createWithBMFont("fonts/bmfont.fnt", "0/0");    _waveLabel->setPosition(Point(this->getContentSize().width/2,                                 this->getContentSize().height/4));    this->addChild(_waveLabel);        return true;}void PlayerStateLabel::setLifeLabel(int life) {    _lifeLabel->setString(Utility::string_format("%d", life));}void PlayerStateLabel::setGoldLabel(int gold) {    _goldLabel->setString(Utility::string_format("%d", gold));}void PlayerStateLabel::setWaveLabel(int thisWave, int totalWave) {    _waveLabel->setString(Utility::string_format("WAVE %d/%d", thisWave, totalWave));}