#include "TowerInfoLayer.h"bool TowerInfoLayer::init(){	if (!Sprite::init() ){		return false;	}        this->initBackgroundAndLabel();        return true;}void TowerInfoLayer::initBackgroundAndLabel() {    this->setSpriteFrame("menu_bottom_bg.png");        auto protraits = Sprite::createWithSpriteFrameName("portraits_sc.png");    protraits->setPosition(Point(protraits->getContentSize().width / 2,                                 this->getContentSize().height / 2));    this->addChild(protraits, TowerInfoLayerDrawingOrderLabel);        _portraitsImage = Sprite::createWithSpriteFrameName("portraits_sc.png");;    _portraitsImage->setPosition(Point(protraits->getContentSize().width / 2,                                       protraits->getContentSize().height / 2));    protraits->addChild(_portraitsImage, TowerInfoLayerDrawingOrderLabel);        _towerName = Label::createWithBMFont("fonts/bmfont.fnt", "");    _towerName->setAnchorPoint(Vec2::ANCHOR_MIDDLE_LEFT);    _towerName->setPosition((Point(protraits->getContentSize().width + TOWERINFO_LAYER_MARGIN,                                   this->getContentSize().height / 2)));    this->addChild(_towerName, TowerInfoLayerDrawingOrderLabel);        auto forceSprite = Sprite::createWithSpriteFrameName("icon_0007.png");    forceSprite->setPosition(Point(this->getContentSize().width * 0.4f,                                   this->getContentSize().height / 2));    this->addChild(forceSprite, TowerInfoLayerDrawingOrderLabel);        _forceLabel = Label::createWithBMFont("fonts/bmfont.fnt", "");//createWithTTF("","fonts/Comic_Book.ttf",18);    _forceLabel->setAnchorPoint(Vec2::ANCHOR_MIDDLE_LEFT);    _forceLabel->setPosition(Point(forceSprite->getPosition().x + forceSprite->getContentSize().width + TOWERINFO_LAYER_MARGIN,                                   this->getContentSize().height / 2));    this->addChild(_forceLabel, TowerInfoLayerDrawingOrderLabel);    //显示攻击速度        auto speedSprite = Sprite::createWithSpriteFrameName("icon_0008.png");    speedSprite->setPosition(Point(this->getContentSize().width * 0.6f,                                   this->getContentSize().height / 2));    this->addChild(speedSprite, TowerInfoLayerDrawingOrderLabel);        _speedLabel = Label::createWithBMFont("fonts/bmfont.fnt", "");//createWithTTF("","fonts/Comic_Book.ttf",18);    _speedLabel->setAnchorPoint(Vec2::ANCHOR_MIDDLE_LEFT);    _speedLabel->setPosition(Point(speedSprite->getPosition().x + speedSprite->getContentSize().width + TOWERINFO_LAYER_MARGIN,                                   this->getContentSize().height / 2));    this->addChild(_speedLabel, TowerInfoLayerDrawingOrderLabel);        //显示攻击范围    auto scopeSprite = Sprite::createWithSpriteFrameName("icon_0009.png");    scopeSprite->setPosition(Point(this->getContentSize().width * 0.8f,                                   this->getContentSize().height / 2));    this->addChild(scopeSprite, TowerInfoLayerDrawingOrderLabel);        _scopeLabel = Label::createWithBMFont("fonts/bmfont.fnt", "");//createWithTTF("","fonts/Comic_Book.ttf",18);    _scopeLabel->setAnchorPoint(Vec2::ANCHOR_MIDDLE_LEFT);    _scopeLabel->setPosition(Point(scopeSprite->getPosition().x + scopeSprite->getContentSize().width + TOWERINFO_LAYER_MARGIN,                                   this->getContentSize().height / 2));    this->addChild(_scopeLabel, TowerInfoLayerDrawingOrderLabel);}void TowerInfoLayer::setTower(BaseTower* tower){    this->setLabelString(tower);}void TowerInfoLayer::setTowerType(int towerType) {    auto baseTower = BaseTower::create();    baseTower->setTowerType(towerType);    this->setLabelString(baseTower);}void  TowerInfoLayer::setLabelString(BaseTower* baseTower) {    _portraitsImage->setSpriteFrame(baseTower->getPortraitsImageName());    _towerName->setString(baseTower->getTowerName());//    _forceLabel->setString(StringUtils::format("%d", baseTower->getForce()));    _speedLabel->setString(StringUtils::format("%d", (int)baseTower->getShootPeriod()));    _scopeLabel->setString(StringUtils::format("%d", (int)baseTower->getAttackScope()));}