#include "Level.h"USING_NS_CC;Level::Level(){};Level::~Level(){};bool Level::initWithLevelAndDifficulty(int level, int difficulty) {    if ( !BaseMap::init(level, difficulty) ) {        return false;    }    	return true;}Level* Level::createGame(int level, int difficulty){      auto layer = new Level();	if (layer && layer->initWithLevelAndDifficulty(level, difficulty)) {		layer->autorelease();		return layer;	}	CC_SAFE_DELETE(layer);    return NULL;}