#include "GroupMonster.h"USING_NS_CC;GroupMonster* GroupMonster::createWithData(int type, int road, int path) {	auto groupMonster = new GroupMonster();	if (groupMonster && groupMonster->init()) {        		groupMonster->setType(type);		groupMonster->setRoad(road);		groupMonster->setPath(path);                groupMonster->autorelease();        		return groupMonster;	}	CC_SAFE_RELEASE(groupMonster);	return NULL;}