#include "MonsterParameter.h"MonsterParameter* MonsterParameter::createWithData(int type,                                                   std::string name,                                                   std::string baseImageName,                                                   std::string bloodColor,                                                   int maxHP,                                                   int money,                                                   int force,                                                   int armor,                                                   int speed,                                                   bool canAttackBySolier,                                                   bool canAttackByTower,                                                   bool haveChildren,                                                   int childrenType,                                                   bool isFly,                                                   std::string portraitsImageName,                                                   std::string portraitsName){    auto monsterParameter = new MonsterParameter();    if (monsterParameter && monsterParameter->init()) {        monsterParameter->setType(type);        monsterParameter->setName(name);        monsterParameter->setBaseImageName(baseImageName);        monsterParameter->setBloodColor(bloodColor);        monsterParameter->setMaxHP(maxHP);        monsterParameter->setMoney(money);        monsterParameter->setForce(force);        monsterParameter->setArmor(armor);        monsterParameter->setSpeed(speed);                monsterParameter->setCanAttackBySolier(canAttackBySolier);        monsterParameter->setCanAttackByTower(canAttackByTower);                monsterParameter->setHaveChildren(haveChildren);        monsterParameter->setChildrenType(childrenType);        monsterParameter->setIsFly(isFly);                monsterParameter->setPortraitsImageName(portraitsImageName);        monsterParameter->setPortraitsName(portraitsName);                return monsterParameter;            }    CC_SAFE_RELEASE(monsterParameter);        return NULL;}