#include "ShopIcon.h"ShopIcon* ShopIcon::createIconWithType(int type){    auto shopIcon = new ShopIcon();        if (shopIcon && shopIcon->initIcon(type))    {        shopIcon->autorelease();        return shopIcon;    }        CC_SAFE_DELETE(shopIcon);    return NULL;}bool ShopIcon::initIcon(int type) {	if (!Sprite::init()) {		return false;	}    	_type = type;    	_normalIcon = Sprite::createWithSpriteFrameName(StringUtils::format("inaps_Icons_000%d.png", type).c_str());	this->addChild(_normalIcon);    	_glowIcon = Sprite::createWithSpriteFrameName(StringUtils::format("inaps_IconsGlow_000%d.png", type).c_str());	_glowIcon->setScale(2.0f);	this->addChild(_glowIcon);        this->setGlowVisible(false);		return true;}void ShopIcon::setGlowVisible(bool visible) {	_glowIcon->setVisible(visible);}bool ShopIcon::getGlowVisible() {    return _glowIcon->isVisible();}Size ShopIcon::getShopIconContentSize() {    return this->_normalIcon->getContentSize();}