#include "TowerUpdateParameter.h"TowerUpdateParameter* TowerUpdateParameter::createWithData(int pid,                                                   int type,                                                   int level,                                                   float value,                                                   std::string onIconImageName,                                                   std::string offIconImageName,                                                   std::string title,                                                   std::string explain){    auto updateParameter = new TowerUpdateParameter();    if (updateParameter && updateParameter->init()) {        updateParameter->setPId(pid);        updateParameter->setType(type);        updateParameter->setLevel(level);        updateParameter->setValue(value);        updateParameter->setOnIconImageName(onIconImageName);        updateParameter->setOffIconImageName(offIconImageName);        updateParameter->setTitle(title);        updateParameter->setExplain(explain);                return updateParameter;            }    CC_SAFE_RELEASE(updateParameter);        return NULL;}