#include "ReinforceSoldier1.h"#include "GameManager.h"ReinforceSoldier1* ReinforceSoldier1::createReinforceSoldier(Point point) {    auto reinforceSoldier = new ReinforceSoldier1();    if (reinforceSoldier && reinforceSoldier->init())    {        reinforceSoldier->setLocation(point);        reinforceSoldier->birth(point);        reinforceSoldier->autorelease();        return reinforceSoldier;    }    CC_SAFE_DELETE(reinforceSoldier);    return NULL;}bool ReinforceSoldier1::init() {    if (! ReinforceSoldierBase::initWithSoldierType(SOLDIER_TYPE_REFINFORCE_SOLDIER_1, 1)) {        return false;    }        return true;}void ReinforceSoldier1::setAnimationAndOthers() {    _animationAttack = AnimationCache::getInstance()->getAnimation("reinforce1_attack");    _animationRun    = AnimationCache::getInstance()->getAnimation("reinforce1_run");    _soldierWaitingSpriteFrame = SpriteFrameCache::getInstance()->getSpriteFrameByName("reinforce_C0_0001.png");}