#include "ReinforceSoldier2.h"#include "GameManager.h"ReinforceSoldier2* ReinforceSoldier2::createReinforceSoldier(Point point){    auto reinforceSoldier = new ReinforceSoldier2();    if (reinforceSoldier && reinforceSoldier->init())    {        reinforceSoldier->setLocation(point);        reinforceSoldier->birth(point);        reinforceSoldier->autorelease();        return reinforceSoldier;    }    CC_SAFE_DELETE(reinforceSoldier);    return NULL;}bool ReinforceSoldier2::init(){    if (! ReinforceSoldierBase::initWithSoldierType(SOLDIER_TYPE_REFINFORCE_SOLDIER_2, 1)) {        return false;    }        return true;}void ReinforceSoldier2::setAnimationAndOthers() {    _animationAttack = AnimationCache::getInstance()->getAnimation("reinforce2_attack");    _animationRun    = AnimationCache::getInstance()->getAnimation("reinforce2_run");    _soldierWaitingSpriteFrame = SpriteFrameCache::getInstance()->getSpriteFrameByName("reinforce_B0_0001.png");}