#include "ReinforceSoldierBase.h"#include "GameManager.h"#include "TowerUpdateManager.h"void ReinforceSoldierBase::birth(Point toPoint){    this->setPosition(toPoint);    this->scheduleUpdate();    this->schedule(schedule_selector(ReinforceSoldierBase::lookingForMonsters),                   this->getAttackPeriod(), -1, this->caculateSpendTime(toPoint));}bool ReinforceSoldierBase::initWithSoldierType(SoldierType soldierType, int level) {    if (!BaseSoldier::initWithSoldierType(soldierType, level)) {        return false;    }            return true;}void ReinforceSoldierBase::addAdditionalParameterConfig() {    this->setForce(this->getForce() * (1 + TowerUpdateManager::getInstance()->getTowerUpgradeValue(UpgradeTowerTagReinforce02_Armor)));    this->setArmor(this->getArmor() * (1 + TowerUpdateManager::getInstance()->getTowerUpgradeValue(UpgradeTowerTagReinforce03_Force)));    this->setCurePercentage(this->getCurePercentage() * (1 + TowerUpdateManager::getInstance()->getTowerUpgradeValue(UpgradeTowerTagReinforce04_CurePercentage)));}