#include "SoldierParameter.h"USING_NS_CC;SoldierParameter* SoldierParameter::createWithData(int type,                                                   std::string name,                                                   std::string baseImageName,                                                   int level,                                                   int maxHP,                                                   int force,                                                   int armor,                                                   int speed,                                                   int scope,                                                   float attackPeriod,                                                   float curePeriod,                                                   float curePercentage) {    auto soldierParameter = new SoldierParameter();    if (soldierParameter && soldierParameter->init()) {        soldierParameter->setType(type);        soldierParameter->setName(name);        soldierParameter->setBaseImageName(baseImageName);        soldierParameter->setMaxHP(maxHP);        soldierParameter->setLevel(level);        soldierParameter->setForce(force);        soldierParameter->setArmor(armor);        soldierParameter->setSpeed(speed);        soldierParameter->setScope(scope);        soldierParameter->setAttackPeriod(attackPeriod);        soldierParameter->setCurePeriod(curePeriod);        soldierParameter->setCurePercentage(curePercentage);                return soldierParameter;            }        CC_SAFE_RELEASE(soldierParameter);        return NULL;}