#include "TowerSoldierBase.h"#include "GameManager.h"#include "TowerUpdateManager.h"bool TowerSoldierBase::initWithSoldierType(SoldierType soldierType, int level){    if (!BaseSoldier::initWithSoldierType(soldierType, level)) {        return false;    }    return true;}void TowerSoldierBase::setSoldierAttackState(float &monsterCurrentHP) {    this->setState(SoldierStateAttack);    monsterCurrentHP =  monsterCurrentHP - this->getForce() + _nearestMonster->getArmor();}void TowerSoldierBase::addAdditionalParameterConfig() {    this->setForce(this->getForce() * (1 + TowerUpdateManager::getInstance()->getTowerUpgradeValue(UpgradeTowerTagSoldier03_Force)));    this->setArmor(this->getArmor() * (1 + TowerUpdateManager::getInstance()->getTowerUpgradeValue(UpgradeTowerTagSoldier01_Armor)));    this->setCurePercentage(this->getCurePercentage() * (1 + TowerUpdateManager::getInstance()->getTowerUpgradeValue(UpgradeTowerTagSoldier04_CurePercentage)));    this->setAttackScope(this->getAttackScope() * (1 + TowerUpdateManager::getInstance()->getTowerUpgradeValue(UpgradeTowerTagSoldier05_AttackScope)));}