#include "SimpleArrowTowerlvl2.h"#include "SimpleArrowTowerlvl3.h"#include "UpdatePanelLayer.h"#include "BaseMap.h"#include "GameManager.h"#include "Arrow.h"bool SimpleArrowTowerlvl2::init() {	if (!BaseArrowTower::init()) {		return false;	}	this->setTowerType(TOWER_TYPE_ARCHER_2);        this->initArrowTowerBase(this->getLevel());	this->schedule(schedule_selector(SimpleArrowTowerlvl2::shoot), this->getShootPeriod());	SoundManager::playArcher2Ready();	return true;}#pragma mark - for update functionvoid SimpleArrowTowerlvl2::updateTower(){	auto simpleArrowTowerlvl3 = SimpleArrowTowerlvl3::create();	simpleArrowTowerlvl3->setTerrain(_terrain);	simpleArrowTowerlvl3->setTag(_terrain->getTag());	simpleArrowTowerlvl3->setPosition(Point(0, 20));	_terrain->addChild(simpleArrowTowerlvl3);        //删除在缓冲子弹队列中的内容    this->refreshBulletVector();	this->removeTower();}void SimpleArrowTowerlvl2::showUpdateMenu(){	auto updatePanelLayer = UpdatePanelLayer::create();	updatePanelLayer->setTag(_terrain->getTag() + UPDATE_MENU_TAG);	updatePanelLayer->setTower(this);	updatePanelLayer->setPosition(this->getParent()->getPosition());	static_cast<BaseMap*>(this->getParent()->getParent())->mTouchLayer->addChild(updatePanelLayer);	if (GameManager::getInstance()->CURRENT_LEVEL <= this->getUpdate1StartLevel()) {		updatePanelLayer->setCanUpdate(false);	}	updatePanelLayer->playShowAnimation();	_isUpdateMenuShown = true;}