#include "TowerParameter.h"USING_NS_CC;TowerParameter* TowerParameter::createWithFileData(                                               int type,                                               std::string name,                                               int   level,                                               float scope,                                               float nextScope,//                                               float force,                                               float buildMoney,                                               float updateMoney,                                               float period,                                               std::string portraitsImageName,                                               int update1StartLevel,                                               int update2StartLevel ){    auto towerParameter = new TowerParameter();    if (towerParameter && towerParameter->init()) {        towerParameter->setType(type);        towerParameter->setName(name);        towerParameter->setLevel(level);        towerParameter->setScope(scope);        towerParameter->setNextScope(nextScope);//        towerParameter->setForce(force);        towerParameter->setBuildMoney(buildMoney);        towerParameter->setUpdateMoney(updateMoney);        towerParameter->setPeriod(period);        towerParameter->setUpdate1StartLevel(update1StartLevel);        towerParameter->setUpdate2StartLevel(update2StartLevel);                towerParameter->setPortraitsImageName(portraitsImageName);                return towerParameter;            }    CC_SAFE_RELEASE(towerParameter);        return NULL;}